allow:
Bash(frontend/src/features/practice/bughunt/BugHunt.css << 'CSS_EOF'

/* ============================================
   TUTORIAL MODE
   ============================================ */

.tutorial-phase-indicator {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-family: 'Orbitron', monospace;
  font-size: 0.75rem;
  color: rgba\(255, 255, 255, 0.3\);
}

.tutorial-phase-indicator span.active {
  color: var\(--neon-cyan\);
  text-shadow: 0 0 10px rgba\(79, 195, 247, 0.5\);
}

.phase-arrow {
  color: rgba\(255, 255, 255, 0.2\);
  font-size: 0.65rem;
}

.tutorial-instruction {
  font-family: 'Orbitron', monospace;
  font-size: 1rem;
  color: var\(--neon-yellow\);
  text-align: center;
  padding: 1rem;
  margin-bottom: 1rem;
  background: rgba\(255, 255, 0, 0.05\);
  border: 1px solid rgba\(255, 255, 0, 0.2\);
  border-radius: 8px;
}

/* Tutorial: 클릭 가능한 코드 뷰어 */
.tutorial-code-viewer {
  font-family: 'JetBrains Mono', 'Fira Code', monospace;
  font-size: 14px;
  line-height: 1.8;
  background: rgba\(0, 0, 0, 0.4\);
  border-radius: 8px;
  padding: 1rem 0;
  overflow-y: auto;
  max-height: 55vh;
}

.code-line {
  display: flex;
  padding: 2px 1rem;
  cursor: pointer;
  transition: background 0.15s, border-left 0.15s;
  border-left: 3px solid transparent;
}

.code-line:hover,
.code-line.hovered {
  background: rgba\(79, 195, 247, 0.08\);
  border-left-color: var\(--neon-cyan\);
}

.code-line.selected-correct {
  background: rgba\(0, 255, 136, 0.15\);
  border-left-color: var\(--neon-green\);
  animation: bugFoundPulse 0.6s ease-out;
}

.code-line.selected-wrong {
  background: rgba\(255, 51, 102, 0.12\);
  border-left-color: var\(--neon-red\);
}

@keyframes bugFoundPulse {
  0% { box-shadow: inset 0 0 0 0 rgba\(0, 255, 136, 0.4\); }
  50% { box-shadow: inset 0 0 30px 0 rgba\(0, 255, 136, 0.15\); }
  100% { box-shadow: inset 0 0 0 0 rgba\(0, 255, 136, 0\); }
}

.line-number {
  width: 40px;
  text-align: right;
  color: #4b5563;
  padding-right: 1rem;
  user-select: none;
  flex-shrink: 0;
}

.line-content {
  margin: 0;
  color: #e8eaed;
  white-space: pre;
}

/* Tutorial: 객관식 */
.tutorial-coaching {
  color: #b0b8c4;
  font-size: 0.9rem;
  line-height: 1.6;
  padding: 1rem;
  background: rgba\(107, 92, 231, 0.08\);
  border-left: 3px solid var\(--nebula-purple\);
  border-radius: 0 8px 8px 0;
  margin-bottom: 1rem;
}

.choice-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
  padding: 1rem;
  max-width: 600px;
  margin: 1rem auto;
}

.choice-btn {
  background: rgba\(255, 255, 255, 0.05\);
  border: 2px solid rgba\(255, 255, 255, 0.15\);
  border-radius: 12px;
  padding: 1rem 1.5rem;
  color: #e8eaed;
  font-size: 0.95rem;
  cursor: pointer;
  transition: all 0.3s;
  text-align: center;
}

.choice-btn:hover:not\(:disabled\) {
  border-color: var\(--neon-cyan\);
  background: rgba\(79, 195, 247, 0.08\);
}

.choice-btn.selected {
  border-color: var\(--neon-magenta\);
  background: rgba\(240, 98, 146, 0.12\);
  box-shadow: 0 0 15px rgba\(240, 98, 146, 0.2\);
}

.choice-btn.correct {
  border-color: var\(--neon-green\);
  background: rgba\(0, 255, 136, 0.12\);
  box-shadow: 0 0 20px rgba\(0, 255, 136, 0.3\);
}

.choice-btn.wrong {
  border-color: var\(--neon-red\);
  background: rgba\(255, 51, 102, 0.12\);
  animation: wrongShake 0.4s ease-out;
}

@keyframes wrongShake {
  0%, 100% { transform: translateX\(0\); }
  25% { transform: translateX\(-8px\); }
  75% { transform: translateX\(8px\); }
}

/* Tutorial: 리뷰 카드 */
.tutorial-review {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  gap: 2rem;
}

.review-card {
  background: rgba\(10, 10, 26, 0.9\);
  border: 2px solid var\(--neon-green\);
  border-radius: 16px;
  padding: 2rem;
  max-width: 600px;
  width: 100%;
  text-align: center;
}

.review-header {
  font-family: 'Orbitron', monospace;
  font-size: 2rem;
  color: var\(--neon-green\);
  text-shadow: 0 0 30px var\(--neon-green\);
  margin-bottom: 1rem;
}

.review-card h3 {
  color: var\(--neon-cyan\);
  font-family: 'Orbitron', monospace;
  font-size: 1.1rem;
  margin-bottom: 0.5rem;
}

.review-explanation {
  color: #e0e0e0;
  line-height: 1.6;
  margin: 1rem 0;
  text-align: left;
}

.review-pattern {
  background: rgba\(0, 0, 0, 0.5\);
  padding: 1rem;
  border-radius: 8px;
  color: var\(--neon-green\);
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.85rem;
  text-align: left;
  white-space: pre-wrap;
  margin-top: 1rem;
}

.stage-clear-btn {
  background: var\(--neon-green\);
  color: #000;
  font-family: 'Orbitron', monospace;
  font-size: 1.1rem;
  font-weight: bold;
  padding: 14px 48px;
  border-radius: 30px;
  border: none;
  cursor: pointer;
  transition: all 0.3s;
  text-transform: uppercase;
  letter-spacing: 2px;
}

.stage-clear-btn:hover {
  transform: scale\(1.05\);
  box-shadow: 0 0 30px rgba\(0, 255, 136, 0.4\);
}

/* ============================================
   GUIDED MODE
   ============================================ */

.guided-step-container {
  padding: 1rem;
}

.guided-code-display {
  background: rgba\(0, 0, 0, 0.4\);
  border-radius: 8px;
  padding: 1rem;
  overflow-x: auto;
  margin: 1rem 0;
}

.guided-code {
  font-family: 'JetBrains Mono', 'Fira Code', monospace;
  font-size: 14px;
  line-height: 1.8;
  color: #e8eaed;
  margin: 0;
  white-space: pre-wrap;
}

.blank-slot {
  display: inline-block;
  min-width: 100px;
  border-bottom: 3px solid var\(--neon-magenta\);
  color: var\(--neon-magenta\);
  font-weight: bold;
  padding: 0 4px;
  animation: blankPulse 1.5s ease-in-out infinite;
}

@keyframes blankPulse {
  0%, 100% { border-color: var\(--neon-magenta\); }
  50% { border-color: var\(--neon-cyan\); }
}

.blank-input-area {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1rem;
  background: rgba\(255, 255, 255, 0.03\);
  border: 1px solid rgba\(255, 255, 255, 0.1\);
  border-radius: 8px;
  margin-top: 1rem;
}

.blank-label {
  font-family: 'Orbitron', monospace;
  font-size: 0.8rem;
  color: var\(--neon-cyan\);
  white-space: nowrap;
}

.blank-input {
  flex: 1;
  background: rgba\(0, 0, 0, 0.5\);
  border: 2px solid rgba\(240, 98, 146, 0.4\);
  border-radius: 8px;
  padding: 10px 15px;
  color: var\(--neon-green\);
  font-family: 'JetBrains Mono', monospace;
  font-size: 1rem;
  transition: border-color 0.3s;
}

.blank-input:focus {
  outline: none;
  border-color: var\(--neon-magenta\);
  box-shadow: 0 0 15px rgba\(240, 98, 146, 0.3\);
}

.blank-input:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* 간략 평가 \(tutorial/guided\) */
.simple-evaluation {
  text-align: center;
  padding: 2rem;
}

.eval-summary {
  background: rgba\(255, 255, 255, 0.05\);
  border: 1px solid rgba\(255, 255, 255, 0.1\);
  border-radius: 12px;
  padding: 2rem;
  display: inline-block;
}

.eval-summary p {
  font-family: 'Orbitron', monospace;
  font-size: 1.2rem;
  color: var\(--neon-cyan\);
  margin: 0.5rem 0;
}
CSS_EOF)
